import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/LiveScores.css';
import { format } from 'date-fns';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSpinner, faClock, faCalendarAlt } from '@fortawesome/free-solid-svg-icons';

const API_BASE_URL = process.env.NODE_ENV === 'development' 
  ? 'http://localhost:3000/api'
  : '/api';

const LiveScores = () => {
  const [liveMatches, setLiveMatches] = useState([]);
  const [previousMatches, setPreviousMatches] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        setIsLoading(true);
        setError(null);
        
        // Fetch live matches
        console.log('Fetching live matches...');
        const liveResponse = await axios.get(`${API_BASE_URL}/matches/live`, {
          withCredentials: true,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          }
        });
        console.log('Live matches response:', liveResponse.data);
        setLiveMatches(Array.isArray(liveResponse.data) ? liveResponse.data : []);

        // Fetch previous matchday
        console.log('Fetching previous matches...');
        const previousResponse = await axios.get(`${API_BASE_URL}/matches/previous`, {
          withCredentials: true,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          }
        });
        console.log('Previous matches response:', previousResponse.data);
        setPreviousMatches(Array.isArray(previousResponse.data) ? previousResponse.data : []);

      } catch (err) {
        console.error('Error fetching matches:', err);
        const errorMessage = err.response?.data?.message || err.message || 'Failed to fetch match data';
        setError(errorMessage);
        // Log detailed error information
        console.error('Error details:', {
          status: err.response?.status,
          statusText: err.response?.statusText,
          data: err.response?.data,
          config: {
            url: err.config?.url,
            method: err.config?.method,
            headers: err.config?.headers
          }
        });
      } finally {
        setIsLoading(false);
      }
    };

    fetchMatches();
    // Set up polling for live matches every 30 seconds
    const interval = setInterval(fetchMatches, 30000);

    return () => clearInterval(interval);
  }, []);

  const renderMatchCard = (match, isLive = false) => {
    if (!match) return null;
    
    return (
      <div 
        key={match._id} 
        className={`match-card ${isLive ? 'live' : ''} ${match.status.toLowerCase()}`}
      >
        <div className="match-header">
          <div className="competition-info">
            <span className="competition-name">{match.competition}</span>
            <span className="match-date">
              <FontAwesomeIcon icon={faCalendarAlt} />
              {format(new Date(match.date), 'dd MMM yyyy')}
            </span>
          </div>
          {isLive && (
            <div className="live-badge">
              LIVE
            </div>
          )}
        </div>

        <div className="match-content">
          <div className="team home">
            <img 
              src={match.homeTeam.logo} 
              alt={match.homeTeam.name} 
              className="team-logo"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = '/placeholder-team-logo.png';
              }}
            />
            <span className="team-name">{match.homeTeam.name}</span>
          </div>

          <div className="score-container">
            <div className="score">
              <span className="score-number">{match.score.homeTeam}</span>
              <span className="score-divider">-</span>
              <span className="score-number">{match.score.awayTeam}</span>
            </div>
            <div className="match-time">
              <FontAwesomeIcon icon={faClock} />
              {isLive ? `${match.minute}'` : format(new Date(match.date), 'HH:mm')}
            </div>
          </div>

          <div className="team away">
            <img 
              src={match.awayTeam.logo} 
              alt={match.awayTeam.name} 
              className="team-logo"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = '/placeholder-team-logo.png';
              }}
            />
            <span className="team-name">{match.awayTeam.name}</span>
          </div>
        </div>
      </div>
    );
  };

  if (isLoading) {
    return (
      <div className="live-scores-container">
        <div className="loading-container">
          <FontAwesomeIcon icon={faSpinner} spin size="2x" />
          <p>Loading matches...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="live-scores-container">
        <div className="error-container">
          <p>Error: {error}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="retry-button"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="live-scores-container">
      <section className="live-matches-section">
        <h2 className="section-title">LIVE MATCHES</h2>
        <div className="matches-grid">
          {liveMatches.length > 0 ? (
            liveMatches.map(match => renderMatchCard(match, true))
          ) : (
            <div className="no-matches-message">
              NO LIVE MATCHES RIGHT NOW
            </div>
          )}
        </div>
      </section>

      <section className="previous-matches-section">
        <h2 className="section-title">Previous MatchDay</h2>
        <div className="matches-grid">
          {previousMatches.length > 0 ? (
            previousMatches.map(match => renderMatchCard(match))
          ) : (
            <div className="no-matches-message">
              NO PREVIOUS MATCHES AVAILABLE
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default LiveScores;
