import React, { useEffect, useState } from 'react';
import socketService from '../services/socketService';
import './LiveMatchTicker.css';

const LiveScores = () => {
    const [liveMatches, setLiveMatches] = useState([]);
    const [previousMatches, setPreviousMatches] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        socketService.connect();
        socketService.subscribe('live_matches', (data) => {
            setLiveMatches(data.filter(m => !m.isUpcoming && (m.status === 'LIVE' || m.status === 'IN_PLAY' || m.status === 'PAUSED')));
            setLoading(false);
        });
        socketService.subscribe('previous_matchday', (data) => {
            setPreviousMatches(data);
        });
        socketService.subscribe('error', (error) => {
            setError(error.message);
            setLoading(false);
        });
        return () => {
            socketService.unsubscribe('live_matches');
            socketService.unsubscribe('previous_matchday');
            socketService.unsubscribe('error');
            socketService.disconnect();
        };
    }, []);

    const formatDate = (utcDate) => {
        const date = new Date(utcDate);
        return date.toLocaleDateString('en-GB', { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric' });
    };
    const formatTime = (utcDate) => {
        const date = new Date(utcDate);
        return date.toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit', hour12: false });
    };

    const renderMatchRow = (match) => (
        <tr key={match._id || match.id} className={`match-row ${match.status === 'LIVE' ? 'live' : match.status === 'FINISHED' ? 'ft' : 'upcoming'}`}> 
            <td>{match.competition}</td>
            <td>{formatDate(match.utcDate)}</td>
            <td>{formatTime(match.utcDate)}</td>
            <td style={{display: 'flex', alignItems: 'center', gap: 8, justifyContent: 'flex-end'}}>
                <img src={match.homeTeam.crest} alt={match.homeTeam.name} className="team-crest" style={{marginRight: 8}} />
                <span className="team-name">{match.homeTeam.name}</span>
            </td>
            <td style={{fontWeight: 700, fontSize: '1.2rem', textAlign: 'center'}}>
                {match.score?.homeScore ?? '-'} - {match.score?.awayScore ?? '-'}
            </td>
            <td style={{display: 'flex', alignItems: 'center', gap: 8}}>
                <span className="team-name">{match.awayTeam.name}</span>
                <img src={match.awayTeam.crest} alt={match.awayTeam.name} className="team-crest" style={{marginLeft: 8}} />
            </td>
            <td>{match.venue}</td>
            <td>{match.status}</td>
        </tr>
    );

    if (loading) {
        return <div className="live-match-ticker loading"><div className="loader"></div><p>Loading live scores...</p></div>;
    }
    if (error) {
        return <div className="live-match-ticker error"><p>{error}</p></div>;
    }

    return (
        <div className="live-scores-page" style={{padding: '32px 0', minHeight: '80vh'}}>
            <h1 style={{marginBottom: 24, color: '#1976d2', fontWeight: 800, textAlign: 'center'}}>Live Scores</h1>
            {liveMatches.length > 0 ? (
                <div className="live-scores-list" style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                    <table style={{width: '100%', maxWidth: 1200, background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px rgba(0,0,0,0.06)', overflow: 'hidden'}}>
                        <thead style={{background: '#e3e6ea'}}>
                            <tr>
                                <th>Competition</th>
                                <th>Date</th>
                                <th>Time</th>
                                <th style={{textAlign: 'right'}}>Home Team</th>
                                <th>Score</th>
                                <th style={{textAlign: 'left'}}>Away Team</th>
                                <th>Venue</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {liveMatches.map(renderMatchRow)}
                        </tbody>
                    </table>
                </div>
            ) : (
                <div style={{marginBottom: 40, textAlign: 'center'}}>
                    <div style={{fontSize: 22, color: '#f44336', fontWeight: 700, marginBottom: 12}}>There are no live matches right now</div>
                    <div style={{fontSize: 18, color: '#1976d2', fontWeight: 700, margin: '32px 0 12px'}}>Previous MatchDay</div>
                    <div className="previous-matches-list" style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                        {previousMatches.length > 0 ? (
                            <table style={{width: '100%', maxWidth: 1200, background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px rgba(0,0,0,0.06)', overflow: 'hidden'}}>
                                <thead style={{background: '#e3e6ea'}}>
                                    <tr>
                                        <th>Competition</th>
                                        <th>Date</th>
                                        <th>Time</th>
                                        <th style={{textAlign: 'right'}}>Home Team</th>
                                        <th>Score</th>
                                        <th style={{textAlign: 'left'}}>Away Team</th>
                                        <th>Venue</th>
                                        <th>Status</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {previousMatches.map(renderMatchRow)}
                                </tbody>
                            </table>
                        ) : <div style={{color: '#888', width: '100%', textAlign: 'center'}}>No previous matches found.</div>}
                    </div>
                </div>
            )}
        </div>
    );
};

export default LiveScores;
