import { io } from 'socket.io-client';

class SocketService {
    constructor() {
        this.socket = null;
        this.listeners = new Map();
        this.connectionAttempts = 0;
        this.maxRetries = 5;
        this.retryDelay = 2000;
        this.debug = true;
        this.isConnecting = false;
        this.reconnectTimer = null;
    }

    connect() {
        if (this.socket || this.isConnecting) return;

        this.isConnecting = true;
        const serverUrl = process.env.REACT_APP_API_URL || window.location.origin;
        const isMobile = this.detectDevice() === 'mobile';

        console.log('Connecting to:', serverUrl, 'Device type:', this.detectDevice());

        try {
            this.socket = io(serverUrl, {
                reconnectionDelay: 1000,
                reconnection: true,
                reconnectionAttempts: 10,
                transports: ['polling', 'websocket'],
                upgrade: true,
                rememberUpgrade: true,
                timeout: 20000,
                withCredentials: true,
                autoConnect: true,
                forceNew: true,
                path: '/socket.io',
                query: {
                    device: this.detectDevice(),
                    timestamp: Date.now()
                }
            });

            // Connection state monitoring
            this.socket.on('connect', () => {
                console.log('Socket connected successfully');
                this.isConnecting = false;
                this.connectionAttempts = 0;
            });

            this.socket.on('connect_error', (error) => {
                console.error('Connection error:', error);
                this.handleConnectionError(error);
            });

            this.socket.on('disconnect', (reason) => {
                console.log('Socket disconnected:', reason);
                if (reason === 'io server disconnect') {
                    // Server initiated disconnect, try reconnecting
                    setTimeout(() => this.connect(), this.retryDelay);
                }
            });

            // Add connection state logging
            this.socket.io.on('ping', () => {
                console.log('Socket ping sent');
            });

            this.socket.io.on('pong', (latency) => {
                console.log('Socket pong received, latency:', latency, 'ms');
            });

        } catch (error) {
            console.error('Error creating socket connection:', error);
            this.handleConnectionError(error);
        }
    }

    detectDevice() {
        const userAgent = navigator.userAgent || navigator.vendor || window.opera;
        if (/android|iPad|iPhone|iPod/i.test(userAgent)) {
            return 'mobile';
        }
        return 'desktop';
    }

    handleConnectionError(error) {
        this.connectionAttempts++;
        this.isConnecting = false;

        console.log(`Connection attempt ${this.connectionAttempts} failed:`, error.message);

        if (this.connectionAttempts >= this.maxRetries) {
            console.error('Max retries reached');
            this.emitError({
                type: 'CONNECTION_ERROR',
                message: 'Unable to connect to the server. Please check your internet connection.'
            });
        } else {
            setTimeout(() => this.connect(), this.retryDelay);
        }
    }

    disconnect() {
        if (this.socket) {
            this.socket.disconnect();
            this.socket = null;
        }
        this.isConnecting = false;
        this.connectionAttempts = 0;
    }

    subscribe(event, callback) {
        if (!this.socket) {
            console.error('Socket not connected');
            return;
        }
        this.socket.on(event, callback);
    }

    unsubscribe(event) {
        if (!this.socket) return;
        this.socket.off(event);
    }

    emitError(error) {
        if (this.socket) {
            this.socket.emit('client_error', error);
        }
    }

    requestLiveMatches() {
        if (!this.socket) {
            console.error('Cannot request matches: No socket connection');
            return;
        }
        console.log('Requesting live matches from server');
        this.socket.emit('request_live_matches');
    }

    subscribeToMatch(matchId) {
        if (!this.socket) this.connect();
        console.log('Subscribing to match:', matchId);
        this.socket.emit('subscribe_match', matchId);
    }

    unsubscribeFromMatch(matchId) {
        if (!this.socket) return;
        console.log('Unsubscribing from match:', matchId);
        this.socket.emit('unsubscribe_match', matchId);
    }
}

export default new SocketService();
