import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Navbar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBars, 
  faMoon, 
  faSun, 
  faBell, 
  faUser, 
  faSignOutAlt,
  faChevronDown,
  faTimes
} from '@fortawesome/free-solid-svg-icons';
import Logo from './Logo';

const Navbar = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const dropdownRef = useRef(null);
  
  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle('dark-theme');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    if (!isMenuOpen) {
      setIsDropdownOpen(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
    setIsDropdownOpen(false);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <nav className={`navbar ${isDarkMode ? 'dark' : ''}`}>
      <div className="navbar-container">
        <div className="navbar-left">
          <Link to="/">
            <Logo />
          </Link>
        </div>

        <div className="navbar-links">
          <Link to="/live-scores">Live Scores</Link>
          <Link to="/fixtures">Fixtures</Link>
          <Link to="/teams">Teams</Link>
          <Link to="/news">News</Link>
          <Link to="/features">Features</Link>
          <Link to="/videos">Videos</Link>
          <Link to="/stats">Stats</Link>
          <Link to="/transfers">Transfers</Link>
          <Link to="/laliga-2024-25">LaLiga 2024-25</Link>
        </div>

        <button className="menu-toggle" onClick={toggleMenu}>
          <FontAwesomeIcon icon={isMenuOpen ? faTimes : faBars} />
        </button>

        <div className={`mobile-menu ${isMenuOpen ? 'active' : ''}`}>
          <div className="mobile-menu-links">
            <Link to="/live-scores" onClick={() => setIsMenuOpen(false)}>Live Scores</Link>
            <Link to="/fixtures" onClick={() => setIsMenuOpen(false)}>Fixtures</Link>
            <Link to="/teams" onClick={() => setIsMenuOpen(false)}>Teams</Link>
            <Link to="/news" onClick={() => setIsMenuOpen(false)}>News</Link>
            <Link to="/features" onClick={() => setIsMenuOpen(false)}>Features</Link>
            <Link to="/videos" onClick={() => setIsMenuOpen(false)}>Videos</Link>
            <Link to="/stats" onClick={() => setIsMenuOpen(false)}>Stats</Link>
            <Link to="/transfers" onClick={() => setIsMenuOpen(false)}>Transfers</Link>
            <Link to="/laliga-2024-25" onClick={() => setIsMenuOpen(false)}>LaLiga 2024-25</Link>
          </div>
        </div>

        <div className="navbar-actions">
          <button className="icon-button" onClick={toggleTheme}>
            <FontAwesomeIcon icon={isDarkMode ? faSun : faMoon} />
          </button>
          <button className="icon-button">
            <FontAwesomeIcon icon={faBell} />
          </button>
          {user ? (
            <div className="user-dropdown" ref={dropdownRef}>
              <button 
                className="user-dropdown-button" 
                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              >
                <FontAwesomeIcon icon={faUser} className="user-icon" />
                <div className="user-info">
                  <span className="username">{user.username}</span>
                  <span className="favorite-team">{user.favoriteTeam}</span>
                </div>
                <FontAwesomeIcon icon={faChevronDown} className="dropdown-icon" />
              </button>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <button onClick={handleLogout} className="dropdown-item">
                    <FontAwesomeIcon icon={faSignOutAlt} />
                    <span>Logout</span>
                  </button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="auth-button">
              <FontAwesomeIcon icon={faUser} />
              <span>Login / Sign Up</span>
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
