import React, { useEffect, useState } from 'react';
import socketService from '../services/socketService';
import './LiveMatchTicker.css';

const LiveScores = () => {
    const [liveMatches, setLiveMatches] = useState([]);
    const [previousMatches, setPreviousMatches] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        socketService.connect();
        socketService.subscribe('live_matches', (data) => {
            setLiveMatches(data.filter(m => !m.isUpcoming && (m.status === 'LIVE' || m.status === 'IN_PLAY' || m.status === 'PAUSED')));
            setLoading(false);
        });
        socketService.subscribe('previous_matchday', (data) => {
            setPreviousMatches(data);
        });
        socketService.subscribe('error', (error) => {
            setError(error.message);
            setLoading(false);
        });
        return () => {
            socketService.unsubscribe('live_matches');
            socketService.unsubscribe('previous_matchday');
            socketService.unsubscribe('error');
            socketService.disconnect();
        };
    }, []);

    const formatDate = (utcDate) => {
        const date = new Date(utcDate);
        return date.toLocaleDateString('en-GB', { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric' });
    };
    const formatTime = (utcDate) => {
        const date = new Date(utcDate);
        return date.toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit', hour12: false });
    };

    const renderMatchDetails = (match) => (
        <div key={match.id} className={`match-card ${match.status === 'LIVE' ? 'live' : match.status === 'FINISHED' ? 'ft' : 'upcoming'}`}> 
            <div className="match-header">
                <span className="competition">{match.competition}</span>
                <span className="match-date">{formatDate(match.utcDate)}</span>
            </div>
            <div className="match-teams">
                <div className="team home">
                    <img src={match.homeTeam.crest} alt={match.homeTeam.name} className="team-crest" />
                    <span className="team-name">{match.homeTeam.name}</span>
                </div>
                <div className="match-score">
                    <div style={{display: 'flex', alignItems: 'center', gap: 4}}>
                        <span className="score">{match.homeTeam.score !== null ? match.homeTeam.score : '-'}</span>
                        <span className="score-separator">-</span>
                        <span className="score">{match.awayTeam.score !== null ? match.awayTeam.score : '-'}</span>
                    </div>
                    <span className="match-time">{formatTime(match.utcDate)}</span>
                    {match.status === 'LIVE' && <span className="live-badge">LIVE</span>}
                </div>
                <div className="team away">
                    <img src={match.awayTeam.crest} alt={match.awayTeam.name} className="team-crest" />
                    <span className="team-name">{match.awayTeam.name}</span>
                </div>
            </div>
            {match.venue && (
                <div className="match-venue">
                    <span>{match.venue}</span>
                </div>
            )}
            {/* Add more details here, e.g. events, stats, etc. */}
        </div>
    );

    if (loading) {
        return <div className="live-match-ticker loading"><div className="loader"></div><p>Loading live scores...</p></div>;
    }
    if (error) {
        return <div className="live-match-ticker error"><p>{error}</p></div>;
    }

    return (
        <div className="live-scores-page" style={{padding: '32px 0', minHeight: '80vh'}}>
            <h1 style={{marginBottom: 24, color: '#1976d2', fontWeight: 800}}>Live Scores</h1>
            {liveMatches.length > 0 ? (
                <div className="live-scores-list" style={{display: 'flex', flexWrap: 'wrap', gap: '24px'}}>
                    {liveMatches.map(renderMatchDetails)}
                </div>
            ) : (
                <div style={{marginBottom: 40}}>
                    <div style={{fontSize: 22, color: '#f44336', fontWeight: 700, marginBottom: 12}}>There are no live matches right now</div>
                    <div style={{fontSize: 18, color: '#1976d2', fontWeight: 700, margin: '32px 0 12px'}}>Previous MatchDay</div>
                    <div className="previous-matches-list" style={{display: 'flex', flexWrap: 'wrap', gap: '24px'}}>
                        {previousMatches.length > 0 ? previousMatches.map(renderMatchDetails) : <div style={{color: '#888'}}>No previous matches found.</div>}
                    </div>
                </div>
            )}
        </div>
    );
};

export default LiveScores;
